data.action:
function defaultBinds() {
    var binds = [
        "dash" => [16, 1],
        "left" => [65, 0],
        "right" => [68, 0],
        "jump" => [32, 0],
        "slam" => [17, 1]
    ];
    binds.set("reverse", reverseBinds(binds));
    return binds;
}

function reverseBinds(binds) {
    var reversed = new StringMap();
    for (k in binds.keys()) {
        if (k == "reverse") continue;
        var v = binds[k];
        reversed.set(v.toString(), k);
    }
    return reversed;
}

function getKey(keycode, keylocation) {
    var keystring = "[" + keycode + "," + keylocation + "]";
    return uKeyBinds.get("reverse").get(keystring);
}

function freeze_frame(seconds) {
    evalXml("<wait seconds='" + seconds + "' modal='false'/>");
}

function freeze_timer(timer) {
    var frozen = uTimers["frozen_timers"];
    if(!Utils.contains(timer, frozen)) frozen.push(timer);
}

function unfreeze_timer(timer) {
    uTimers.get("frozen_timers").remove(timer);
}
